DROP TABLE IF EXISTS terminations;
DROP TABLE IF EXISTS timeoff;
DROP TABLE IF EXISTS patterns;
DROP TABLE IF EXISTS commsettings;
DROP TABLE IF EXISTS users;
create table users (
	emp_id INTEGER PRIMARY KEY,
	mgr BOOLEAN,
	last_name VARCHAR(50),
	first_name VARCHAR(50),
	auth_id VARCHAR(50),
	profile_pic TEXT,
	gmail_user VARCHAR(50),
	phone VARCHAR(50),
	position VARCHAR(50),
	doe DATE,
	address VARCHAR(50),
	city VARCHAR(16),
	state VARCHAR(50),
	zip INTEGER,
	email VARCHAR(50)
);

create table commsettings (
	commsettings_id	serial primary key,		
	sms	boolean DEFAULT false,		
	email boolean	DEFAULT false,		
	emp_id	integer
);

create table patterns
(
    pattern_id serial primary key,
    emp_id integer,
    sun varchar(10),
    mon varchar(10),
    tue varchar(10),
    wed varchar(10),
    thu varchar(10),
    fri varchar(10),
    sat varchar(10)
);

CREATE TABLE timeoff 
(
    timeoff_id SERIAL PRIMARY KEY,
    emp_id INTEGER,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL,
    status VARCHAR(120),
    reason VARCHAR(200),
    request_type VARCHAR(80)
);

CREATE table terminations
(
    termination_id serial primary key,
    emp_id integer,
    reason varchar(240),
    termination_date date,
    last_name varchar(180),
    first_name varchar(180),
    position varchar(180),
    doe date,
    profile_pic text,
    phone varchar(80)
);

SELECT *
FROM users;